import { Cloudinary } from '@cloudinary/url-gen';

// Cloudinary yapılandırması
const cld = new Cloudinary({
  cloud: {
    cloudName: 'your-cloud-name' // Bu değeri kendi Cloudinary cloud name'iniz ile değiştirin
  }
});

export const uploadImage = async (file) => {
  try {
    // FormData oluştur
    const formData = new FormData();
    formData.append('file', file);
    formData.append('upload_preset', 'your-upload-preset'); // Bu değeri kendi upload preset'iniz ile değiştirin

    // Cloudinary'ye yükle
    const response = await fetch(
      `https://api.cloudinary.com/v1_1/your-cloud-name/image/upload`,
      {
        method: 'POST',
        body: formData
      }
    );

    if (!response.ok) {
      throw new Error('Resim yükleme başarısız');
    }

    const data = await response.json();
    return data.secure_url; // Yüklenen resmin URL'sini döndür
  } catch (error) {
    console.error('Cloudinary yükleme hatası:', error);
    throw error;
  }
};

export default cld;
