import React, { useEffect, useState } from 'react';
import { carService } from '../../services/api';

const AdminCars = () => {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState({ model: '', year: 2020 });

  const load = async () => {
    const { data } = await carService.getAll();
    setItems(data || []);
  };

  useEffect(() => { load(); }, []);

  const onDelete = async (id) => {
    if (!window.confirm('Silmek istediğinize emin misiniz?')) return;
    await carService.delete(id);
    load();
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await carService.create(form);
    setForm({ model: '', year: 2020 });
    load();
  };

  return (
    <div>
      <h2>Arabalar</h2>
      <form onSubmit={onSubmit} style={{ display: 'flex', gap: 8, marginBottom: 16 }}>
        <input placeholder="Model" value={form.model} onChange={(e)=>setForm({...form, model:e.target.value})} />
        <input type="number" placeholder="Yıl" value={form.year} onChange={(e)=>setForm({...form, year:Number(e.target.value)})} />
        <button type="submit">Ekle</button>
      </form>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Model</th>
            <th>Yıl</th>
            <th>Aksiyon</th>
          </tr>
        </thead>
        <tbody>
          {items.map((x) => (
            <tr key={x.id || x.car_id}>
              <td>{x.id || x.car_id}</td>
              <td>{x.model}</td>
              <td>{x.year}</td>
              <td>
                <button onClick={() => onDelete(x.id || x.car_id)}>Sil</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminCars;
