import React, { useState } from 'react';
import Header from '../components/Header';
import Banner from '../components/Banner';
import SearchBar from '../components/SearchBar';
import CarGrid from '../components/CarGrid';
import Footer from '../components/Footer';

const carBrands = [
  'Toyota',
  'Honda',
  'Ford',
  'Chevrolet',
  'BMW',
  'Audi',
  'Mercedes',
  'Volkswagen',
];

function Homepage({ isLoggedIn }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedBrand, setSelectedBrand] = useState('');

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleBrandSelect = (brand) => {
    setSelectedBrand(brand);
    setSearchQuery(''); // reset search when brand selected
  };

  return (
    <>
      <Header isLoggedIn={isLoggedIn} />
      <Banner />
      <SearchBar onSearch={handleSearch} />
      <div style={{ display: 'flex', padding: '2rem', gap: '2rem', justifyContent: 'center', backgroundColor: '#e0e7ff', borderRadius: '12px' }}>
        <aside style={{
          width: '280px',
          backgroundColor: '#f0f4f8',
          borderRadius: '8px',
          padding: '1rem',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
          height: 'calc(100vh - 300px)',
          overflowY: 'auto',
          fontWeight: '600',
          color: '#1e40af',
        }}>
          <h3>Car Brands</h3>
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            <li
              style={{
                padding: '0.5rem',
                cursor: 'pointer',
                backgroundColor: selectedBrand === '' ? '#dbeafe' : 'transparent',
                borderRadius: '4px',
                marginBottom: '0.25rem',
                transition: 'background-color 0.3s ease',
              }}
              onClick={() => handleBrandSelect('')}
            >
              All Brands
            </li>
            {carBrands.map((brand) => (
              <li
                key={brand}
                style={{
                  padding: '0.5rem',
                  cursor: 'pointer',
                  backgroundColor: selectedBrand === brand ? '#dbeafe' : 'transparent',
                  borderRadius: '4px',
                  marginBottom: '0.25rem',
                  transition: 'background-color 0.3s ease',
                }}
                onClick={() => handleBrandSelect(brand)}
              >
                {brand}
              </li>
            ))}
          </ul>
        </aside>
        <main style={{ flex: 1, maxWidth: '50vw', display: 'flex', justifyContent: 'center', minHeight: '600px' }}>
          <CarGrid searchQuery={searchQuery} selectedBrand={selectedBrand} />
        </main>
      </div>

      <Footer />
    </>
  );
}

export default Homepage;
