import React from 'react';
import { Link, Outlet, useLocation } from 'react-router-dom';

const AdminLayout = () => {
  const location = useLocation();
  const isActive = (path) => (location.pathname.includes(path) ? { fontWeight: 'bold', textDecoration: 'underline' } : {});

  return (
    <div style={{ display: 'flex', minHeight: '100vh', background: '#f7f8fa', paddingTop: 80 }}>
      <aside style={{ width: 240, padding: 16, borderRight: '1px solid #eee', background: '#fff' }}>
        <h3 style={{ marginTop: 0 }}>Admin Paneli</h3>
        <nav style={{ display: 'flex', flexDirection: 'column', gap: 10 }}>
          <Link to="/admin/ads" style={isActive('/admin/ads')}>İlanlar</Link>
          <Link to="/admin/cars" style={isActive('/admin/cars')}>Arabalar</Link>
          <Link to="/admin/brands" style={isActive('/admin/brands')}>Markalar</Link>
          <Link to="/admin/colors" style={isActive('/admin/colors')}>Renkler</Link>
          <Link to="/admin/users" style={isActive('/admin/users')}>Kullanıcılar</Link>
        </nav>
      </aside>
      <main style={{ flex: 1, padding: 24 }}>
        <div style={{ background: '#fff', borderRadius: 12, padding: 20, boxShadow: '0 6px 20px rgba(0,0,0,0.06)' }}>
          <Outlet />
        </div>
      </main>
    </div>
  );
};

export default AdminLayout;
