import React from 'react';
import { Link, Outlet, useLocation } from 'react-router-dom';

const AdminLayout = () => {
  const location = useLocation();
  const isActive = (path) => (location.pathname.includes(path) ? { fontWeight: 'bold', textDecoration: 'underline' } : {});

  return (
    <div style={{ position: 'relative', minHeight: '100vh', paddingTop: 80, background: "url('https://images.unsplash.com/photo-1503376780353-7e6692767b70?auto=format&fit=crop&w=1920&q=80') no-repeat center center/cover" }}>
      <div style={{ position: 'absolute', inset: 0, background: 'rgba(0,0,0,0.45)' }} />
      <div style={{ position: 'relative', display: 'flex' }}>
      <aside style={{ width: 240, padding: 16, borderRight: '1px solid #eee', background: 'rgba(255,255,255,0.96)', backdropFilter: 'blur(6px)' }}>
        <h3 style={{ marginTop: 0 }}>Admin Paneli</h3>
        <nav style={{ display: 'flex', flexDirection: 'column', gap: 10 }}>
          <Link to="/admin/ads" style={isActive('/admin/ads')}>İlanlar</Link>
          <Link to="/admin/cars" style={isActive('/admin/cars')}>Arabalar</Link>
          <Link to="/admin/brands" style={isActive('/admin/brands')}>Markalar</Link>
          <Link to="/admin/colors" style={isActive('/admin/colors')}>Renkler</Link>
          <Link to="/admin/users" style={isActive('/admin/users')}>Kullanıcılar</Link>
        </nav>
      </aside>
      <main style={{ flex: 1, padding: 24 }}>
        <div style={{ background: 'rgba(255,255,255,0.98)', borderRadius: 12, padding: 20, boxShadow: '0 6px 20px rgba(0,0,0,0.12)', backdropFilter: 'blur(6px)' }}>
          <Outlet />
        </div>
      </main>
      </div>
    </div>
  );
};

export default AdminLayout;
