import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar({ isLoggedIn, siteName }) {
  const [scrolled, setScrolled] = useState(false);
  const [visible, setVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      
      // Scroll yönünü belirle
      if (currentScrollY > lastScrollY) {
        // Aşağı scroll
        setVisible(false);
      } else {
        // Yukarı scroll
        setVisible(true);
      }

      // Navbar stilini güncelle
      if (currentScrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
      
      setLastScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  return (
    <nav className={`navbar${scrolled ? ' scrolled' : ''}${visible ? ' visible' : ' hidden'}`}>
      <div className="navbar-left">
        <div className="site-name">{siteName || 'Nereye'}</div>
      </div>
      <div className="navbar-right">
        <Link to="/home" className="nav-link">Home</Link>
        <Link to="/login" className="nav-link">Login</Link>
        <Link to="/register" className="nav-link">Kayıt Ol</Link>
        {isLoggedIn && <Link to="/profile" className="nav-link">Profile</Link>}
      </div>
    </nav>
  );
}

export default Navbar;
