import React, { useEffect, useState } from 'react';
import { advertisementService } from '../../services/api';

const AdminAds = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [form, setForm] = useState({ title: '', description: '', price: 0 });

  const load = async () => {
    setLoading(true);
    setError('');
    try {
      const { data } = await advertisementService.getAll();
      setItems(data || []);
    } catch (e) {
      setError('İlanlar yüklenemedi');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, []);

  const onDelete = async (id) => {
    if (!window.confirm('Silmek istediğinize emin misiniz?')) return;
    await advertisementService.delete(id);
    load();
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await advertisementService.create(form);
    setForm({ title: '', description: '', price: 0 });
    load();
  };

  return (
    <div>
      <h2>İlanlar</h2>
      <form onSubmit={onSubmit} style={{ display: 'flex', gap: 8, marginBottom: 16 }}>
        <input placeholder="Başlık" value={form.title} onChange={(e)=>setForm({...form, title:e.target.value})} />
        <input placeholder="Açıklama" value={form.description} onChange={(e)=>setForm({...form, description:e.target.value})} />
        <input type="number" placeholder="Fiyat" value={form.price} onChange={(e)=>setForm({...form, price:Number(e.target.value)})} />
        <button type="submit">Ekle</button>
      </form>
      {loading ? 'Yükleniyor...' : error || (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Başlık</th>
              <th>Fiyat</th>
              <th>Aksiyon</th>
            </tr>
          </thead>
          <tbody>
            {items.map((x) => (
              <tr key={x.id}>
                <td>{x.id}</td>
                <td>{x.title || x.ad_title}</td>
                <td>{x.price}</td>
                <td>
                  <button onClick={() => onDelete(x.id)}>Sil</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminAds;
