import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import carService from '../services/carService';
import './Homepage.css';

const Homepage = () => {
  const [cars, setCars] = useState([]);
  const [filteredCars, setFilteredCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedBrand, setSelectedBrand] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCars = async () => {
      try {
        const response = await carService.getAllCars();
        setCars(response.data);
        setFilteredCars(response.data);
        setLoading(false);
      } catch (err) {
        setError('Araçlar yüklenirken bir hata oluştu.');
        setLoading(false);
      }
    };

    fetchCars();
  }, []);

  useEffect(() => {
    let result = cars;

    if (searchTerm) {
      result = result.filter(car =>
        car.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
        car.model.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (selectedBrand) {
      result = result.filter(car => car.brand === selectedBrand);
    }

    setFilteredCars(result);
  }, [searchTerm, selectedBrand, cars]);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleBrandSelect = (brand) => {
    setSelectedBrand(brand === selectedBrand ? '' : brand);
  };

  const handleRentClick = (carId) => {
    navigate(`/rent/${carId}`);
  };

  const brands = [...new Set(cars.map(car => car.brand))];

  if (loading) {
    return (
      <div className="homepage">
        <Navbar />
        <div className="loading">Yükleniyor...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="homepage">
        <Navbar />
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="homepage">
      <Navbar isLoggedIn={true} siteName="Nereye" fontFamily="Poppins" />
      <div className="banner">
        <h1>Hayalinizdeki Araca Kavuşun</h1>
        <p>En uygun fiyatlarla, en kaliteli araçları sizlerle buluşturuyoruz.</p>
      </div>

      <div className="search-container">
        <div className="search-bar">
          <i className="fas fa-search"></i>
          <input
            type="text"
            placeholder="Araç ara..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
      </div>

      <div className="content-wrapper">
        <div className="brand-filter">
          <h3>Markalar</h3>
          <ul className="brand-list">
            {brands.map(brand => (
              <li
                key={brand}
                className={selectedBrand === brand ? 'active' : ''}
                onClick={() => handleBrandSelect(brand)}
              >
                <i className="fas fa-car"></i>
                {brand}
              </li>
            ))}
          </ul>
        </div>

        <div className="car-list">
          {filteredCars.length > 0 ? (
            filteredCars.map(car => (
              <div key={car.id} className="car-card">
                <img src={car.image} alt={`${car.brand} ${car.model}`} className="car-image" />
                <div className="car-details">
                  <h3>{car.brand} {car.model}</h3>
                  <div className="car-info">
                    <span><i className="fas fa-calendar"></i> {car.year}</span>
                    <span><i className="fas fa-tachometer-alt"></i> {car.mileage} km</span>
                  </div>
                  <div className="car-price">
                    {car.price} ₺ <span>/ gün</span>
                  </div>
                  <button
                    className="rent-button"
                    onClick={() => handleRentClick(car.id)}
                  >
                    Kirala
                  </button>
                </div>
              </div>
            ))
          ) : (
            <div className="no-cars">
              Aradığınız kriterlere uygun araç bulunamadı.
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
