import React, { useEffect, useState } from 'react';
import { colorService } from '../../services/api';

const AdminColors = () => {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState({ color_name: '' });

  const load = async () => {
    const { data } = await colorService.getAll();
    setItems(data || []);
  };

  useEffect(() => { load(); }, []);

  const onDelete = async (id) => {
    if (!window.confirm('Silmek istediğinize emin misiniz?')) return;
    await colorService.delete(id);
    load();
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await colorService.create(form);
    setForm({ color_name: '' });
    load();
  };

  return (
    <div>
      <h2>Renkler</h2>
      <form onSubmit={onSubmit} style={{ display: 'flex', gap: 8, marginBottom: 16 }}>
        <input placeholder="Renk adı" value={form.color_name} onChange={(e)=>setForm({...form, color_name:e.target.value})} />
        <button type="submit">Ekle</button>
      </form>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Ad</th>
            <th>Aksiyon</th>
          </tr>
        </thead>
        <tbody>
          {items.map((x) => (
            <tr key={x.id || x.color_id}>
              <td>{x.id || x.color_id}</td>
              <td>{x.color_name || x.name}</td>
              <td>
                <button onClick={() => onDelete(x.id || x.color_id)}>Sil</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminColors;
